function thegem_theme_remove_shortcodes() {
	if(!function_exists('thegem_scripts')) {
		global $shortcode_tags;
		$shortcodes = $shortcode_tags;
		ksort($shortcodes);
		foreach ($shortcodes as $shortcode => $value) {
			if(strpos($shortcode, 'gem_') === 0 || strpos($shortcode, 'thegem_') === 0) {
				remove_shortcode($shortcode);
			}
		}
	}
}

if(!function_exists('thegem_is_loop_builder_template')) {
	function thegem_is_loop_builder_template() {
		if ((!empty($GLOBALS['thegem_template_type']) && $GLOBALS['thegem_template_type'] == 'loop-item') || thegem_is_template_post('loop-item')) {
			return true;
		}

		return false;
	}
}

function thegem_cf_get_edit_template_type()
{
	$type = get_post_type(get_the_ID());
	if (!empty($_REQUEST['post_type'])){
		$type = $_REQUEST['post_type'];
	} elseif(empty($_REQUEST['post_type']) && !empty($_REQUEST['post_id'])){
		$type = get_post_type($_REQUEST['post_id']);
	}

	if (thegem_is_template_post('single-post') ||
		!empty($_REQUEST['post_type']) && $_REQUEST['post_type'] == 'thegem_templates' && !empty($_REQUEST['p']) && thegem_get_template_type($_REQUEST['p']) == 'single-post') {
		$type = 'post';
	}

	if (thegem_is_template_post('loop-item') ||
		(!empty($GLOBALS['thegem_template_type']) && $GLOBALS['thegem_template_type'] == 'loop-item') ||
		(!empty($_REQUEST['post_type']) && $_REQUEST['post_type'] == 'thegem_templates' && !empty($_REQUEST['p']) && thegem_get_template_type($_REQUEST['p']) == 'loop-item')) {
		$type = 'post';
	}

	if (thegem_is_template_post('portfolio') ||
		!empty($_REQUEST['post_type']) && $_REQUEST['post_type'] == 'thegem_templates' && !empty($_REQUEST['p']) && thegem_get_template_type($_REQUEST['p']) == 'portfolio') {
		$type = 'thegem_pf_item';
	}

	if (thegem_is_template_post('single-product') ||
		!empty($_REQUEST['post_type']) && $_REQUEST['post_type'] == 'thegem_templates' && !empty($_REQUEST['p']) && thegem_get_template_type($_REQUEST['p']) == 'single-product') {
		$type = 'product';
	}

	if($type === 'blocks') {
		if(get_post_meta(get_the_ID(), 'thegem_is_portfolio', true)) {
			$type = 'thegem_pf_item';
		}
		if(get_post_meta(get_the_ID(), 'thegem_is_single_post', true)) {
			$type = 'post';
		}
		if(get_post_meta(get_the_ID(), 'thegem_is_single_product', true)) {
			$type = 'product';
		}
	}

	return $type;
}

function thegem_cf_get_edit_template_post_id()
{
	$post_id = get_the_ID();

	switch (thegem_cf_get_edit_template_type()) {
		case 'post':
			$single_post = thegem_templates_init_post();
			$post_id = !empty($single_post->ID) ? $single_post->ID : $post_id;
			break;
		case 'thegem_pf_item':
			$portfolio = thegem_templates_init_portfolio();
			$post_id = !empty($portfolio->ID) ? $portfolio->ID : $post_id;
			break;
		case 'product':
			$product = thegem_templates_init_product();
			$post_id = (!empty($product) && !empty($product->get_id())) ? $product->get_id() : $post_id;
			break;
	}

	return $post_id;
}

function thegem_cf_get_post_types()
{
	$post_types = array();
	foreach (get_post_types(array('public' => true), 'object') as $slug => $post_type) {
		if (!in_array($slug, array('thegem_news', 'thegem_footer', 'thegem_title', 'thegem_templates', 'attachment'), true)) {
			$post_types[$post_type->label] = $slug;
		}
	}

	return $post_types;
}

function thegem_cf_get_fields_by_post_type($pt, $param = 'list')
{
	$post_type = $pt;
	switch ($pt) {
		case 'page':
			$post_type = 'default';
			break;
		case 'thegem_pf_item':
			$post_type = 'portfolio';
			break;
	}

	if (!function_exists('thegem_theme_options_get_page_settings')) return;

	$pt_data = thegem_theme_options_get_page_settings($post_type);
	$custom_fields = !empty($pt_data['custom_fields']) ? $pt_data['custom_fields'] : null;
	$custom_fields_data = !empty($pt_data['custom_fields_data']) ? json_decode($pt_data['custom_fields_data'], true) : null;

	$data = array();
	if (!empty($custom_fields_data)) {
		foreach ($custom_fields_data as $field) {
			switch ($param) {
				case 'list':
					$data[$field['title']] = $field['key'];
					break;
				case 'link':
					if($field['type'] == 'link') {
						$data[$field['title']] = $field['key'];
					}
					break;
			}
		}
	}

	return $data;
}

function thegem_cf_get_project_details($param = 'list')
{
	if (empty(thegem_get_option('portfolio_project_details_data'))) return;

	$meta_data = json_decode(thegem_get_option('portfolio_project_details_data'), true);

	$project_details = array();
	if (!empty($meta_data)) {
		foreach ($meta_data as $meta) {
			$key = __($meta['title'], 'thegem');
			$value = '_thegem_cf_'.str_replace('-', '_', sanitize_title($meta['title']));

			switch ($param) {
				case 'list':
					$project_details[$key] = $value;
					break;
				case 'link':
					if(isset($meta['type']) && $meta['type'] == 'link') {
						$project_details[$key] = $value;
					}
					break;
			}
		}
	}

	return $project_details;
}

function thegem_cf_get_acf_plugin_groups($post_type = 'any')
{
	if (!thegem_is_plugin_active('advanced-custom-fields/acf.php') && !thegem_is_plugin_active('advanced-custom-fields-pro/acf.php')) return array();

	$groups = array();
	if ($post_type == 'any') {
		$field_groups = acf_get_field_groups();
	} else {
		$field_groups = acf_get_field_groups(array('post_type' => $post_type));
	}

	foreach ($field_groups as $group) {
		if (!empty($group)) {
			$groups[$group['title'] . ' (ACF)'] = $group['key'];
		}
	}

	return $groups;
}

function thegem_cf_get_acf_plugin_groups_keys()
{
	if (!thegem_is_plugin_active('advanced-custom-fields/acf.php') && !thegem_is_plugin_active('advanced-custom-fields-pro/acf.php')) return array();

	$groups = array();
	foreach (acf_get_field_groups() as $group) {
		if (!empty($group)) {
			$groups[] = $group['key'];
		}
	}

	return $groups;
}

function thegem_cf_get_acf_plugin_fields_by_group($gr, $param = 'list')
{
	if (!thegem_is_plugin_active('advanced-custom-fields/acf.php') && !thegem_is_plugin_active('advanced-custom-fields-pro/acf.php')) return array();

	$fields = array();
	foreach (acf_get_fields($gr) as $field) {
		switch ($param) {
			case 'list':
				if (!empty($field)) {
					$fields[$field['label']] = $field['name'];
				}
				break;
			case 'link':
				if (!empty($field) && ($field['type'] == 'link' || $field['type'] == 'url' || $field['type'] == 'file')) {
					$fields[$field['label']] = $field['name'];
				}
				break;
		}
	}

	return $fields;
}

function thegem_cf_trim_plugins_group_slug($group) {
	preg_match('/_(.*?)_/', $group, $match);

	return !empty($match) ? str_replace($match[0], '', $group) : $group;
}

function thegem_cf_check_plugins_group($group) {
	preg_match('/_(.*?)_/', $group, $match);

	return !empty($match) ? $match[1] : $group;
}

function thegem_cf_get_toolset_plugin_groups() {
	if (!thegem_is_plugin_active('types/wpcf.php')) return array();

	$groups = array();
	if (!empty(wpcf_admin_fields_get_groups())) {
		foreach (wpcf_admin_fields_get_groups() as $group) {
			if (!empty($group)) {
				$groups[$group['name'] . ' (Toolset)'] = '_toolset_' . $group['slug'];
			}
		}
	}

	return $groups;
}

function thegem_cf_get_toolset_plugin_fields_by_group($gr, $output = 'list') {
	if (!thegem_is_plugin_active('types/wpcf.php')) return array();

	$trim_gr = thegem_cf_trim_plugins_group_slug($gr);

	$toolset_groups = wpcf_admin_fields_get_groups();
	$toolset_fields = [];
	if (!empty($toolset_groups)) {
		foreach($toolset_groups as $group){
			if($group['slug'] == $trim_gr){
				$toolset_fields[] = wpcf_admin_fields_get_fields_by_group($group['id']);
			}
		}
	}

	$fields = array();
	$i = 0;
	if (!empty($toolset_fields)) {
		foreach ($toolset_fields as $field) {
			if (!empty($field)) {
				foreach ($field as $fd) {
					switch ($output) {
						case 'list':
							$fields[$fd['name']] = $fd['meta_key'];
							break;
						case 'link':
							if (!empty($fd) && ($fd['type'] == 'link' || $fd['type'] == 'url')) {
								$fields[$fd['name']] = $fd['meta_key'];
							}
							break;
						case 'type':
							$i++;
							if (!isset($fields[$fd['type']])) {
								$fields[$fd['type']] = $fd['meta_key'];
							} else {
								$fields[$fd['type'] . '_' . $i] = $fd['meta_key'];
							}

							break;
					}
				}
			}
		}
	}

	return $fields;
}

function thegem_cf_get_fields_is_type_link($fields_type)
{
	$data = [];

	switch ($fields_type) {
		case 'custom_fields':
			$post_types = thegem_cf_get_post_types();
			foreach ($post_types as $pt) {
				$fields = thegem_cf_get_fields_by_post_type($pt, 'link');
				if (!empty($fields)) {
					foreach ($fields as $key => $value) {
						$data[$key] = $value;
					}
				}
			}
			break;
		case 'project_details':
			$data = thegem_cf_get_project_details('link');
			break;
		default:
			$fields_acf = thegem_cf_get_acf_plugin_fields_by_group($fields_type, 'link');
			$fields_toolset = thegem_cf_get_toolset_plugin_fields_by_group($fields_type, 'link');
			$fields = array_merge($fields_acf, $fields_toolset);
			if (!empty($fields)) {
				foreach ($fields as $key => $value) {
					$data[$key] = $value;
				}
			}
			break;
	}

	return $data;
}

