	$result[] = array(
		'type' => 'dropdown',
		'heading' => __('Skin', 'thegem'),
		'param_name' => 'skin',
		'value' => array_merge(array(
				__('Modern', 'thegem') => 'modern',
				__('Table', 'thegem') => 'table',
				__('Labels', 'thegem') => 'labels',
			)
		),
		'std' => thegem_is_loop_builder_template() ? 'table' : 'modern',
		'edit_field_class' => 'vc_column vc_col-sm-12',
