			)
		),
		'std' => 'horizontal',
		'dependency' => array(
			'element' => 'skin',
			'value' => array('modern', 'labels'),
		),
		'edit_field_class' => 'vc_column vc_col-sm-12',
		'group' => $group
	);

	foreach (thegem_cf_get_post_types() as $pt) {
		$repeater_controls = thegem_cf_set_repeater_controls('post_type', $pt);
		$result[] = array(
			'type' => 'param_group',
			'heading' => __('Items', 'thegem'),
			'param_name' => $pt . '_info_content',
			'params' => $repeater_controls,
			'dependency' => array(
				'element' => 'post_type',
				'value' => $pt
			),
			'description' => (empty($repeater_controls) ? __('Fields not found.', 'thegem') . ' ' : '') . __('Go to the <a href="' . get_site_url() . '/wp-admin/admin.php?page=thegem-theme-options#/single-pages" target="_blank">Theme Options -> Single Pages</a> to manage your custom fields.', 'thegem'),
			'group' => $group
		);
	}

	$repeater_controls = thegem_cf_set_repeater_controls('project_details');
	$result[] = array(
		'type' => 'param_group',
		'heading' => __('Items', 'thegem'),
		'param_name' => 'project_details_info_content',
		'params' => $repeater_controls,
		'dependency' => array(
			'element' => 'source',
			'value' => 'project_details'
		),
		'description' => (empty($repeater_controls) ? __('Fields not found.', 'thegem') . ' ' : '') . __('Go to the <a href="' . get_site_url() . '/wp-admin/admin.php?page=thegem-theme-options#/single-pages/portfolio/panel.portfolio_project_details" target="_blank">Theme Options -> Portfolio Page -> Project Details</a> to manage your project details.', 'thegem'),
		'group' => $group
	);

	$result[] = array(
		'type' => 'param_group',
		'heading' => __('Items', 'thegem'),
		'param_name' => 'info_content',
		'params' => thegem_cf_set_repeater_controls('manual_input'),
		'dependency' => array(
			'element' => 'source',
			'value' => 'manual_input'
		),
		'group' => $group
	);

	if ((thegem_is_plugin_active('advanced-custom-fields/acf.php') || thegem_is_plugin_active('advanced-custom-fields-pro/acf.php')) && !empty(thegem_cf_get_acf_plugin_groups())) {
		foreach (thegem_cf_get_acf_plugin_groups() as $gr) {
			$repeater_controls = thegem_cf_set_repeater_controls('acf', $gr);
			$result[] = array(
				'type' => 'param_group',
				'heading' => __('Items', 'thegem'),
				'param_name' => $gr . '_info_content',
				'params' => $repeater_controls,
				'dependency' => array(
					'element' => 'source',
					'value' => $gr
				),
				'description' => (empty($repeater_controls) ? __('Fields not found.', 'thegem') . ' ' : '') . __('Go to the <a href="' . get_site_url() . '/wp-admin/edit.php?post_type=acf-field-group" target="_blank">ACF -> Field Groups</a> to manage your custom fields.', 'thegem'),
				'group' => $group
			);
		}
	}

	// Toolset Group Fields Repeater
	if ((thegem_is_plugin_active('types/wpcf.php')) && !empty(thegem_cf_get_toolset_plugin_groups())) {
		foreach (thegem_cf_get_toolset_plugin_groups() as $gr) {
			$repeater_controls = thegem_cf_set_repeater_controls('toolset', $gr);
			$result[] = array(
				'type' => 'param_group',
				'heading' => __('Items', 'thegem'),
				'param_name' => $gr . '_info_content',
				'params' => $repeater_controls,
				'dependency' => array(
					'element' => 'source',
					'value' => $gr
				),
				'description' => (empty($repeater_controls) ? __('Fields not found.', 'thegem') . ' ' : '') . __('Go to the <a href="' . get_site_url() . '/wp-admin/admin.php?page=types-custom-fields" target="_blank">Toolset -> Custom Fields</a> to manage your custom fields.', 'thegem'),
				'group' => $group
			);
		}
	}

	return $result;
}

