		'layout_background_color' => '',
		'layout_border' => '',
		'layout_border_width' => '',
		'layout_border_radius' => '',
		'layout_border_color' => '',
		'icon_color' => '',
		'icon_size' => '',
		'icon_spacing' => '',
		'icon_spacing_vertical' => '',
		'text_layout' => 'inline',
		'label_text_spacing' => '',
		'label_text_style' => '',
		'label_text_font_weight' => '',
		'label_text_font_style' => '',
		'label_text_letter_spacing' => '',
		'label_text_transform' => '',
		'label_text_color' => '',
