		'value_text_spacing' => '',
		'value_text_style' => '',
		'value_text_font_weight' => '',
		'value_text_font_style' => '',
		'value_text_letter_spacing' => '',
		'value_text_transform' => '',
		'value_text_color' => '',
		'value_text_color_hover' => '',
		'container_layout_tablet_padding_top' => '',
		'container_layout_tablet_padding_bottom' => '',
		'container_layout_tablet_padding_left' => '',
		'container_layout_tablet_padding_right' => '',
		'container_layout_mobile_padding_top' => '',
		'container_layout_mobile_padding_bottom' => '',
		'container_layout_mobile_padding_left' => '',
		'container_layout_mobile_padding_right' => '',
		'container_layout_tablet_margin_top' => '',
		'container_layout_tablet_margin_bottom' => '',
		'container_layout_tablet_margin_left' => '',
		'container_layout_tablet_margin_right' => '',
		'container_layout_mobile_margin_top' => '',
		'container_layout_mobile_margin_bottom' => '',
		'container_layout_mobile_margin_left' => '',
		'container_layout_mobile_margin_right' => '',
	),
		thegem_templates_extra_options_extract(),
		thegem_templates_responsive_options_extract()
	), $atts, 'gem_custom_fields');

	$uniqid = uniqid('thegem-custom-') . rand(1, 9999);
	$post_id = thegem_cf_get_edit_template_post_id();

	if (!empty($atts)) {
		$params = array_merge($params, $atts);
	}

	$info_content = $meta_data = $meta_types = array();
	if (!empty($params['source']) && $params['source'] == 'thegem_cf') {
		$post_type = $params['post_type'];
		$info_content = !empty($params[$post_type . '_info_content']) ? vc_param_group_parse_atts($params[$post_type . '_info_content']) : '';
		$meta_data = array_flip(thegem_cf_get_fields_by_post_type($post_type));
	}

	if (!empty($params['source']) && $params['source'] == 'project_details') {
		$info_content = !empty($params['project_details_info_content']) ? vc_param_group_parse_atts($params['project_details_info_content']) : '';
		$meta_data = array_flip(thegem_cf_get_project_details());
	}

	if (!empty($params['source']) && $params['source'] == 'manual_input') {
		$info_content = vc_param_group_parse_atts($params['info_content']);
	}

	if (!empty($params['source']) && $params['source'] != 'thegem_cf' && $params['source'] != 'project_details' && $params['source'] != 'manual_input'){
		$plugin_type = thegem_cf_check_plugins_group($params['source']);

		if ((thegem_is_plugin_active('advanced-custom-fields/acf.php') || thegem_is_plugin_active('advanced-custom-fields-pro/acf.php')) && $plugin_type != 'toolset') {
			$source = $params['source'];
			$info_content = !empty($params[$source . '_info_content']) ? vc_param_group_parse_atts($params[$source . '_info_content']) : '';
			$meta_data = array_flip(thegem_cf_get_acf_plugin_fields_by_group($source));
		}

		if (thegem_is_plugin_active('types/wpcf.php') && $plugin_type == 'toolset') {
			$source = $params['source'];
			$info_content = !empty($params[$source . '_info_content']) ? vc_param_group_parse_atts($params[$source . '_info_content']) : '';
			$meta_data = array_flip(thegem_cf_get_toolset_plugin_fields_by_group($source));
			$meta_types = array_flip(thegem_cf_get_toolset_plugin_fields_by_group($source, 'type'));
		}
	}

	// Layout Design Options
	$params['layout_design_wrap_class'] = '';
	if (!empty($atts['container_layout'])) {
		$custom_css_class = vc_shortcode_custom_css_class($atts['container_layout']);
		$params['layout_design_wrap_class'] = ' '.$custom_css_class;
		$params['layout_design_wrap_class'] = apply_filters( VC_SHORTCODE_CUSTOM_CSS_FILTER_TAG, $params['layout_design_wrap_class'], 'gem_custom_fields', $atts );
	}

	$skin = 'custom-fields--' . $params['skin'];
	$layout = 'custom-fields--' . $params['layout'];
	$alignment = 'custom-fields--' . $params['list_alignment'];
	$divider = !empty($params['list_divider']) ? 'custom-fields--divider-show' : 'custom-fields--divider-hide';
	$colon = !empty($params['label_colon']) ? 'custom-fields--colon-show' : 'custom-fields--colon-hide';
	$text_layout = 'custom-fields--text-' . $params['text_layout'];
	$loop_item = thegem_is_loop_builder_template() ? 'custom-fields--loop-item' : '';
	$params['element_class'] = implode(' ', array(
		$skin,
		$layout,
		$alignment,
		$divider,
		$colon,
		$text_layout,
		$loop_item,
		$params['element_class'],
		$params['layout_design_wrap_class'],
		thegem_templates_responsive_options_output($params)
	));
	if(empty($params['label_text_style']) && thegem_is_loop_builder_template()) {
		$params['label_text_style'] = 'text-body-tiny';
	}
	if(empty($params['value_text_style']) && thegem_is_loop_builder_template()) {
		$params['value_text_style'] = 'text-body-tiny';
	}
	$label_text_styled = implode(' ', array($params['label_text_style'], $params['label_text_font_weight']));
	$value_text_styled = implode(' ', array($params['value_text_style'], $params['value_text_font_weight']));

	ob_start();
	?>

	<div <?php if (!empty($params['element_id'])): ?>id="<?= esc_attr($params['element_id']); ?>"<?php endif; ?> class="thegem-te-custom-fields <?= esc_attr($params['element_class']); ?> <?= esc_attr($uniqid); ?>">
		<?php if (!empty($info_content)) : ?>
			<div class="custom-fields">
				<?php foreach ($info_content as $item) {
					$meta_output = $label_output = $value_output = '';
					$meta_value = !empty($item['select_field']) ? get_post_meta($post_id, $item['select_field'], true) : '';
					$meta_label = (!empty($meta_data) && !empty($item['select_field']) && isset($meta_data[$item['select_field']])) ? $meta_data[$item['select_field']] : '';

					if (!empty($meta_value)) {
						if (!empty($item['icon']) && $item['icon'] == 'custom' && isset($item['icon_pack']) && !empty($item['icon_' . str_replace("-", "", $item['icon_pack'])])) {
							wp_enqueue_style('icons-' . $item['icon_pack']);
							$label_output .= '<div class="icon">' . thegem_build_icon($item['icon_pack'], $item['icon_' . str_replace("-", "", $item['icon_pack'])]) . '</div>';
						}
						if (!empty($item['icon']) && $item['icon'] == 'image' && !empty($item['image_file'])) {
							$label_output .= '<div class="icon"><img src="' . thegem_attachment_url($item['image_file']) . '" alt="#" /></div>';
						}
						if (!empty($item['icon']) && $item['icon'] == 'svg' && !empty($item['image_file'])) {
							$label_output .= '<div class="icon">'.thegem_get_svg_content($item['image_file']).'</div>';
						}

						if (!empty($item['label'])) {
							$label_text = !empty($item['label_text']) ? $item['label_text'] : $meta_label;
							$label_output .= '<div class="label-before">' . esc_html($label_text) . '<span class="colon">:</span></div>';
						}
						$label_output = '<div class="item-label ' . $label_text_styled . '">' . $label_output . '</div>';
						$is_map_field = false;

						if (!empty($item['field_type']) && !empty($item['select_field'])){
							// ACF / Toolset inherit type fields
							if ($item['field_type'] == 'inherit'){
								if($plugin_type != 'toolset') {
									$field_type = get_field_object($item['select_field'])['type'];
								} else {
									$get_type = isset($meta_types[$item['select_field']]) ? $meta_types[$item['select_field']] : '';
									$field_type = !empty(strstr($get_type, '_', true)) ? strstr($get_type, '_', true) : $get_type;
								}

								switch ($field_type) {
									case 'image':
										$id = attachment_url_to_postid( $meta_value );
										if (!empty($id)) {
											$image = wp_get_attachment_image($id, 'full', false, []);
										} else {
											$image = wp_get_attachment_image($meta_value, 'full', false, []);
										}

										$value_output = !empty($image) ? '<div class="meta">' . $image . '</div>' : '';
										break;
									case 'file':
										$file = wp_get_attachment_url($meta_value);
										$value_output = !empty($file) ? '<div class="meta">' . $file . '</div>' : '';
										break;
									case 'oembed':
										$width = get_field_object($item['select_field'])['width'];
										$styles = !empty($width) ? 'style="width: ' . $width . 'px; max-width: 100%;"' : '';

										ob_start();
										echo get_field_object($item['select_field'])['value'];
										$value_output = '<div class="meta" '.$styles.'>' . ob_get_clean() . '</div>';
										break;
									case 'embed' :
										global $wp_embed;

										ob_start();
										echo $wp_embed->run_shortcode('[embed]' . $meta_value . '[/embed]');
										$value_output = '<div class="meta">' . ob_get_clean() . '</div>';
										break;
									case 'video' :
										ob_start();
										echo wp_video_shortcode( ['src' => $meta_value] );
										$value_output = '<div class="meta">' . ob_get_clean() . '</div>';
										break;
									case 'checkbox' :
										if (!empty($item['field_type_inherit_display']) && $item['field_type_inherit_display'] == 'table'){
											$checkbox_css = '';
											$resolution = array('desktop', 'tablet', 'mobile');
											$checkbox_customize = '.thegem-te-custom-fields.'.$uniqid;
											$marker = !empty($item['field_type_inherit_table_bullet']) ? $item['field_type_inherit_table_bullet'] : 'default';

											$checkbox_css .= $checkbox_customize . ' .custom-fields-item {flex: 1;}';
											$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value {width: 100%;}';
											foreach ($resolution as $res) {
												if (!empty($item['field_type_inherit_table_cols_'.$res]) || strcmp($item['field_type_inherit_table_cols_'.$res], '0') === 0) {
													$result = str_replace(' ', '', $item['field_type_inherit_table_cols_'.$res]);
													if ($res == 'desktop') {
														$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value .meta-items-list li {width: calc(100% / '.$result.') !important;}';
													} else {
														$width = ($res == 'tablet' ? 'max-width: 1023px' : 'max-width: 767px');
														$checkbox_css .= ' @media screen and ('.$width.') {'.$checkbox_customize.' .custom-fields-item .item-value .meta-items-list li {width: calc(100% / '.$result.') !important;}}';
													}
												}
											}
											if (!empty($item['field_type_inherit_table_bullet_color'])) {
												$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value .meta-items-list li:before {color: '.$item['field_type_inherit_table_bullet_color'].' !important;}';
											}

											ob_start();
											if (is_array($meta_value) && !empty($meta_value)) {
												foreach ($meta_value as $value){
													echo '<li>' . $value . '</li>';
												}
											} else {
												echo $meta_value;
											}

											$checkbox = ob_get_clean();
											if(!empty($checkbox_css)) {
												$checkbox_css = '<style>'.$checkbox_css.'</style>';
											}
											$value_output = !empty($checkbox) ? '<ul class="meta-items-list list-marker--'. $marker .'">' . $checkbox . '</ul>' . $checkbox_css : '';
										} else {
											$checkbox = is_array($meta_value) && !empty($meta_value) ? implode(', ', $meta_value) : $meta_value;
											$value_output = !empty($checkbox) ? '<div class="meta">' . $checkbox . '</div>' : '';
										}

										break;
									case 'checkboxes' :
										$checkboxes_list = [];
										if (is_array($meta_value)) {
											foreach ($meta_value as $key => $values) {
												foreach ($values as $value) {
													$checkboxes_list[] = $value;
												}
											}
										}

										if (!empty($item['field_type_inherit_display']) && $item['field_type_inherit_display'] == 'table'){
											$checkbox_css = '';
											$resolution = array('desktop', 'tablet', 'mobile');
											$checkbox_customize = '.thegem-te-custom-fields.'.$uniqid;
											$marker = !empty($item['field_type_inherit_table_bullet']) ? $item['field_type_inherit_table_bullet'] : 'default';

											$checkbox_css .= $checkbox_customize . ' .custom-fields-item {flex: 1;}';
											$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value {width: 100%;}';
											foreach ($resolution as $res) {
												if (!empty($item['field_type_inherit_table_cols_'.$res]) || strcmp($item['field_type_inherit_table_cols_'.$res], '0') === 0) {
													$result = str_replace(' ', '', $item['field_type_inherit_table_cols_'.$res]);
													if ($res == 'desktop') {
														$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value .meta-items-list li {width: calc(100% / '.$result.') !important;}';
													} else {
														$width = ($res == 'tablet' ? 'max-width: 1023px' : 'max-width: 767px');
														$checkbox_css .= ' @media screen and ('.$width.') {'.$checkbox_customize.' .custom-fields-item .item-value .meta-items-list li {width: calc(100% / '.$result.') !important;}}';
													}
												}
											}
											if (!empty($item['field_type_inherit_table_bullet_color'])) {
												$checkbox_css .= $checkbox_customize . ' .custom-fields-item .item-value .meta-items-list li:before {color: '.$item['field_type_inherit_table_bullet_color'].' !important;}';
											}

											ob_start();
											foreach ($checkboxes_list as $value){
												echo '<li>' . $value . '</li>';
											}

											$checkbox = ob_get_clean();
											if(!empty($checkbox_css)) {
												$checkbox_css = '<style>'.$checkbox_css.'</style>';
											}
											$value_output = !empty($checkbox) ? '<ul class="meta-items-list list-marker--'. $marker .'">' . $checkbox . '</ul>' . $checkbox_css : '';
										} else {
											$checkboxes = is_array($checkboxes_list) && !empty($checkboxes_list) ? implode(', ', $checkboxes_list) : '';
											$value_output = !empty($checkboxes) ? '<div class="meta">' . $checkboxes . '</div>' : '';
										}

										break;
									case 'google_map' :
										$location = $meta_value;
										$value_output = '';
										if($location) {
											wp_enqueue_script('thegem-acf-google-maps');
											$value_output .= '<div class="acf-map" data-zoom="16">';
											$value_output .= '<div class="marker" data-lat="' . esc_attr($location['lat']) . '" data-lng="' . esc_attr($location['lng']) . '"></div>';
											$value_output .= '</div>';
											$is_map_field = true;
										}
										$value_output = '<div class="meta">' . $value_output . '</div>';
										break;
									default:
										$value_output = !is_array($meta_value) ? '<div class="meta">' . $meta_value . '</div>' : '';
								}
							} elseif ($item['field_type'] == 'number') {
								$meta_value = floatval($meta_value);
								$decimal = explode('.', $meta_value);
								$decimal = isset($decimal[1]) ? strlen(($decimal[1])) : 0;
								$decimal = $decimal <= 3 ? $decimal : 3;

								if (!empty($item['field_format']) && $item['field_format'] == 'wp_locale') {
									$meta_value = number_format_i18n($meta_value, $decimal);
								}

								if (!empty($item['field_prefix'])) {
									$meta_value = $item['field_prefix'] . '' . $meta_value;
								}

								if (!empty($item['field_suffix'])) {
									$meta_value = $meta_value . '' . $item['field_suffix'];
								}

								$value_output = '<div class="meta">' . $meta_value . '</div>';
							} else {
								$value_output = !is_array($meta_value) ? '<div class="meta">' . $meta_value . '</div>' : '';
							}
							if ($item['field_type'] == 'text') {
								if ($plugin_type == 'acf') {
									$field_type = get_field_object($item['select_field'])['type'];
									if($field_type === 'google_map' && $location = $meta_value) {
										$value_output = '<div class="acf-map-address">' . esc_html($location['address']) . '</div>';
										$value_output = '<div class="meta">' . $value_output . '</div>';
									}
								}
							}
						}

						if (!empty($item['link'])) {
							if ($item['link'] == 'custom') {
								$custom_link = !empty($item['link_custom']) ? vc_build_link($item['link_custom']) : null;

								if (!empty($custom_link['url'])) {
									$value_output = '<a href="' . $custom_link['url'] . '" class="meta-link" target="' . $custom_link['target'] . '" title="' . $custom_link['title'] . '" rel="' . $custom_link['rel'] . '">' . $value_output . '</a>';
								}
							} else {
								$dynamic_link = [
									'url' => '',
									'target' => !empty($item['link_target']) ? '_blank' : '_self',
									'rel' => !empty($item['link_nofollow']) ? 'nofollow' : '',
								];

								switch ($item['link_dynamic']) {
									case 'custom_fields':
										if (!empty($item['custom_fields_link_select'])) {
											$dynamic_link['url'] = get_post_meta($post_id, $item['custom_fields_link_select'], true);
										}

										break;
									case 'project_details':
										if (!empty($item['project_details_link_select'])) {
											$dynamic_link['url'] = get_post_meta($post_id, $item['project_details_link_select'], true);
										}

										break;
									default:
										if (!empty($item[$item['link_dynamic'].'_link_select'])) {
											$acf_link = get_post_meta($post_id, $item[$item['link_dynamic'].'_link_select'], true);
											$acf_obj = get_field_object($item[$item['link_dynamic'].'_link_select'], $post_id);

											if(!empty($acf_obj) && $acf_obj['type'] === 'file' && !empty($acf_obj['value'])) {
												$dynamic_link = [
													'url' => !empty($acf_obj['value']['url']) ? $acf_obj['value']['url'] : null,
													'target' => $dynamic_link['target'],
													'rel' => $dynamic_link['rel'],
												];
											} elseif(is_array($acf_link)){
												$dynamic_link = [
													'url' => !empty($acf_link['url']) ? $acf_link['url'] : null,
													'target' => !empty($acf_link['target']) ? $acf_link['target'] : $dynamic_link['target'],
													'rel' => !empty($acf_link['rel']) ? $acf_link['rel'] : $dynamic_link['rel'],
												];
											} else{
												$dynamic_link['url'] = $acf_link;
											}
										}

										break;
								}

								if (!empty($dynamic_link['url'])) {
									$value_output = '<a href="' . $dynamic_link['url'] . '" class="meta-link" target="' . $dynamic_link['target'] . '" rel="' . $dynamic_link['rel'] . '">' . $value_output . '</a>';
								}
							}
						}

						if (!empty($item['value_line_through'])) {
							$value_text_styled .= ' text-line-through';
						}

						$value_output = '<div class="item-value ' . $value_text_styled . '">' . do_shortcode($value_output) . '</div>';

