function thegem_project_info_shortcode($atts, $content) {
	extract(shortcode_atts(array(
		'style' => '',
	), $atts, 'thegem_project_info'));

	$values = vc_param_group_parse_atts($atts['values']);
	$graph_lines_data = array();
	$post_id = thegem_cf_get_edit_template_post_id();

	foreach ( $values as $data ) {
		$meta_label = $meta_value = $select_field = '';

		if (!isset($data['source'])) {
			$data['source'] = 'default';
		}

		if (!isset($data['icon'])) {
			$data['icon'] = '1';
		}

		if (!isset($data['icon_pack'])) {
			$data['icon_pack'] = $data['pack'];
		}

		switch ($data['source']) {
			case 'thegem_cf':
				$meta_data = array_flip(thegem_cf_get_fields_by_post_type($data['post_type']));
				$select_field = !empty($data[$data['post_type'].'_select_field']) ? $data[$data['post_type'].'_select_field'] : '';
				$meta_value = !empty($select_field) ? get_post_meta($post_id, $select_field, true) : '';
				$meta_label = (!empty($meta_data) && !empty($select_field) && isset($meta_data[$select_field])) ? $meta_data[$select_field] : '';

				break;
			case 'project_details':
				$meta_data = array_flip(thegem_cf_get_project_details());
				$select_field = !empty($data['project_details_select_field']) ? $data['project_details_select_field'] : '';
				$meta_value = !empty($select_field) ? get_post_meta($post_id, $select_field, true) : '';
				$meta_label = (!empty($meta_data) && !empty($select_field) && isset($meta_data[$select_field])) ? $meta_data[$select_field] : '';

				break;
			case 'manual_input':
				$select_field = !empty($data['manual_input_select_field']) ? $data['manual_input_select_field'] : '';
				$meta_value = !empty($select_field) ? get_post_meta($post_id, $select_field, true) : '';

				break;
			default:
				if (!empty($data[$data['source'].'_select_field'])) {
					$plugin_type = thegem_cf_check_plugins_group($data['source']);
					$select_field = $data[$data['source'] . '_select_field'];

					if ($plugin_type !== 'toolset') {
						$meta_data = array_flip(thegem_cf_get_acf_plugin_fields_by_group($data['source']));
					} else {
						$meta_data = array_flip(thegem_cf_get_toolset_plugin_fields_by_group($data['source']));
					}

					$meta_value = !empty($select_field) ? get_post_meta($post_id, $select_field, true) : '';
					$meta_label = (!empty($meta_data) && !empty($select_field) && isset($meta_data[$select_field])) ? $meta_data[$select_field] : '';
				}

				break;
		}

		if ($data['source'] != 'default' && !empty($select_field) && !empty($data['field_type'])){
			if ($data['field_type'] == 'number') {
				$meta_value = floatval($meta_value);
				$decimal = explode('.', $meta_value);
				$decimal = isset($decimal[1]) ? strlen(($decimal[1])) : 0;
				$decimal = $decimal <= 3 ? $decimal : 3;

				if (!empty($data['field_format']) && $data['field_format'] == 'wp_locale') {
					$meta_value = number_format_i18n($meta_value, $decimal);
				}

				if (!empty($data['field_prefix'])) {
					$meta_value = $data['field_prefix'] . '' . $meta_value;
				}

				if (!empty($data['field_suffix'])) {
					$meta_value = $meta_value . '' . $data['field_suffix'];
				}
			}

			if (!empty($data['link'])) {
				if ($data['link'] == 'custom') {
					$custom_link = !empty($data['link_custom']) ? vc_build_link($data['link_custom']) : null;

					if (!empty($custom_link['url'])) {
						$meta_value = '<a href="' . $custom_link['url'] . '" class="meta-link" target="' . $custom_link['target'] . '" title="' . $custom_link['title'] . '" rel="' . $custom_link['rel'] . '">' . $meta_value . '</a>';
					}
				} else {
					$dynamic_link = [
						'url' => '',
						'target' => !empty($data['link_target']) ? '_blank' : '_self',
						'rel' => !empty($data['link_nofollow']) ? 'nofollow' : '',
					];

					switch ($data['link_dynamic']) {
						case 'custom_fields':
							if (!empty($data['custom_fields_link_select'])) {
								$dynamic_link['url'] = get_post_meta($post_id, $data['custom_fields_link_select'], true);
							}

							break;
						case 'project_details':
							if (!empty($data['project_details_link_select'])) {
								$dynamic_link['url'] = get_post_meta($post_id, $data['project_details_link_select'], true);
							}

							break;
						default:
							if (!empty($data[$data['link_dynamic'].'_link_select'])) {
								$acf_link = get_post_meta($post_id, $data[$data['link_dynamic'].'_link_select'], true);
								$acf_obj = get_field_object($data[$data['link_dynamic'].'_link_select'], $post_id);

								if(!empty($acf_obj) && $acf_obj['type'] === 'file' && !empty($acf_obj['value'])) {
									$dynamic_link = [
										'url' => !empty($acf_obj['value']['url']) ? $acf_obj['value']['url'] : null,
										'target' => $dynamic_link['target'],
										'rel' => $dynamic_link['rel'],
									];
								} elseif(is_array($acf_link)){
									$dynamic_link = [
										'url' => !empty($acf_link['url']) ? $acf_link['url'] : null,
										'target' => !empty($acf_link['target']) ? $acf_link['target'] : $dynamic_link['target'],
										'rel' => !empty($acf_link['rel']) ? $acf_link['rel'] : $dynamic_link['rel'],
									];
								} else{
									$dynamic_link['url'] = $acf_link;
								}
							}

							break;
					}

					if (!empty($dynamic_link['url'])) {
						$meta_value = '<a href="' . $dynamic_link['url'] . '" class="meta-link" target="' . $dynamic_link['target'] . '" rel="' . $dynamic_link['rel'] . '">' . $meta_value . '</a>';
					}
				}
			}

			$new_line['title'] = !empty($data['label']) && !empty($meta_label) ? $meta_label : '';
			$new_line['decription'] = !empty($meta_value) ? $meta_value : '';
		} elseif ($data['source'] == 'default') {
			$new_line['title'] =  isset($data['title']) ? ($data['title']) : '';
			$new_line['decription'] = isset($data['decription']) ? ($data['decription']) : '';
		}

		$new_line['icon'] = !empty($data['icon']) ? $data['icon'] : '';
		$new_line['pack'] = isset($data['icon_pack']) ? ($data['icon_pack']) : '';
		$new_line['material'] = isset($data['icon_material']) ? ($data['icon_material']) : '';
		$new_line['elegant'] = isset($data['icon_elegant']) ? ($data['icon_elegant']) : '';
		$new_line['fontawesome'] = isset($data['icon_fontawesome']) ? ($data['icon_fontawesome']) : '';
		$new_line['thegem-header'] = isset($data['icon_thegemheader']) ? ($data['icon_thegemheader']) : '';
		$new_line['thegemdemo'] = isset($data['icon_thegemdemo']) ? ($data['icon_thegemdemo']) : '';
		$new_line['userpack'] = isset($data['icon_userpack']) ? ($data['icon_userpack']) : '';
		$new_line['icon_color'] = isset($data['icon_color']) ? ($data['icon_color']) : '';

		$graph_lines_data[] = $new_line;
		if ($new_line['pack'] == 'elegant') {wp_enqueue_style( 'icons-elegant');}
		if ($new_line['pack'] == 'material') {wp_enqueue_style( 'icons-material');}
		if ($new_line['pack'] == 'fontawesome') {wp_enqueue_style( 'icons-fontawesome');}
		if ($new_line['pack'] == 'thegem-header') {wp_enqueue_style( 'icons-thegem-header');}
		if ($new_line['pack'] == 'thegemdemo') {wp_enqueue_style( 'icons-thegemdemo');}
		if ($new_line['pack'] == 'userpack') {wp_enqueue_style( 'icons-userpack');}
	}

	$output = '';
	foreach ($graph_lines_data as $line) {
		$color = 'background-color:' . $line['icon_color'] . '; color:' . $line['icon_color'] . ';';
		$output .= '<div class="project-info-shortcode-item">';

		if ($line['pack'] == 'elegant') {
			$icon = ($line['elegant']);
		} elseif ($line['pack'] == 'material') {
			$icon = ($line['material']);
		} elseif ($line['pack'] == 'thegem-header') {
			$icon = ($line['thegem-header']);
		} elseif ($line['pack'] == 'thegemdemo') {
			$icon = ($line['thegemdemo']);
		} elseif ($line['pack'] == 'userpack') {
			$icon = ($line['userpack']);
		} else {
			$icon = ($line['fontawesome']);
		}

		if($line['icon']) {
			$output .= '<div style="' . $color . '" class="icon gem-print-icon gem-icon-pack-' . $line['pack'] . '">&#x' . $icon . '</div>';
		}

		$output .= '<div class="title">' . $line['title'] . '</div>';
		if (!empty($line['decription'])) {
			$output .= '<div class="decription">' . $line['decription'] . '</div>';
		}
		$output .= '</div>';
	}

	if ($style == 2) {
		$calsses = 'project-info-shortcode project-info-shortcode-style-2';
	} else {
		$calsses = 'project-info-shortcode project-info-shortcode-style-default';
	}

	$return_html = "<div class='$calsses'>" .$output. "</div>";
	return $return_html;
}

