function thegem_gallery_shortcode($atts) {

	if (!isset($atts['new_default_highlights'])) {
		if (!isset($atts['gallery_ignore_highlights'])) {
			$atts['gallery_ignore_highlights'] = '';
		}
	}

	$atts = vc_map_get_attributes('gem_gallery', $atts);

	$atts['columns_desktop'] = $atts['gallery_layout'];
	unset($atts['gallery_layout']);
	$atts['columns_100'] = $atts['gallery_fullwidth_columns'];
	unset($atts['gallery_fullwidth_columns']);

	foreach ($atts as $key => $value) {
		if (strpos($key, 'gallery_') !== false) {
			$atts[str_replace('gallery_','', $key)] = $value;
			unset($atts[$key]);
		}
	}

	ob_start();

	if($atts['type'] == 'slider') {
		$atts['layout'] = $atts['slider_layout'];
		unset($atts['slider_layout']);
		thegem_gallery($atts);
	} else {
		$atts['layout'] = $atts['style'];
		unset($atts['gallery_style']);
		thegem_gallery_block($atts);
	}

	$return_html = trim(preg_replace('/\s\s+/', ' ', ob_get_clean()));
	return $return_html;
}

function TheGemGalleryAutocomplete($query) {
	$out = [];
	$galleries = thegem_vc_get_galleries();
	$query = strtolower($query);

	if (!empty($galleries)) {
		foreach ($galleries as $key => $value) {
			if (str_contains(strtolower($key), $query) || str_contains($value, $query)) {
				$data = array();
				$data['value'] = $value;
				$data['label'] = $key;
				$out[] = $data;
			}
		}
	}

	return $out;
}

function TheGemGalleryExact($query) {
	$data = [];
	$query = $query['value'];
	$query = intval($query);
	$galleries = array_flip(thegem_vc_get_galleries());

	if (!empty($galleries) && !empty($galleries[$query])) {
		$data['value'] = $query;
		$data['label'] = $galleries[$query];
	}

	return !empty($data) ? $data : false;
}

function thegem_vc_get_galleries() {
	$galleries_posts = get_posts(array(
		'post_type' => 'thegem_gallery',
		'posts_per_page' => -1,
		'orderby' => 'title',
		'order' => 'ASC',
	));
	$galleries = array();
	foreach($galleries_posts as $gallery) {
		$galleries[$gallery->post_title.' (ID='.$gallery->ID.')'] = $gallery->ID;
	}
	return $galleries;
}

