	function get_additional_meta_fields($meta_fields_acf, $post_type = 'portfolio', $slider = false) {
		if ($post_type == 'post') {
			$std_tax = 'category';
			$taxonomies = select_post_type_taxonomies();
			$custom_fields = select_theme_options_custom_fields_all(true);
			$description = __('Go to the <a href="' . get_site_url() . '/wp-admin/admin.php?page=thegem-theme-options#/single-pages/" target="_blank">Theme Options -> Single Pages</a> to manage your custom fields.', 'thegem');
		} else {
			$std_tax = 'thegem_portfolios';
			$taxonomies = select_post_type_taxonomies('thegem_pf_item', true);
			$custom_fields = select_theme_options_custom_fields('portfolio', true);
			$description = __('Go to the <a href="' . get_site_url() . '/wp-admin/admin.php?page=thegem-theme-options#/single-pages/portfolio" target="_blank">Theme Options -> Single Pages -> Portfolio Page</a> to manage your custom fields.', 'thegem');
		}

		if ($slider) {
			$click_behavior = array(
				array(
					'type' => 'thegem_hidden_param',
					'param_name' => 'additional_meta_click_behavior',
					'value' => 'archive_link',
					'edit_field_class' => 'taxonomies_behavior vc_column vc_col-sm-6',
					'group' => __('Appearance', 'thegem')
				),
				array(
					'type' => 'thegem_hidden_param',
					'param_name' => 'additional_meta_click_behavior_meta',
					'value' => 'disabled',
					'edit_field_class' => 'meta_behavior vc_column vc_col-sm-6',
					'group' => __('Appearance', 'thegem')
				)
			);
		} else {
			if (thegem_is_template_post('blog-archive')) {
				$click_behavior_default = 'archive_link';
			} else {
				$click_behavior_default = 'filtering';
			}
			$click_behavior = array(
				array(
