					'std' => $std_tax,
					'dependency' => array(
						'element' => 'additional_meta_type',
						'value' => array('taxonomies')
					),
					'edit_field_class' => 'vc_column vc_col-sm-6',
					'group' => __('Appearance', 'thegem')
				),
				array(
					'type' => 'dropdown',
					'heading' => __('Select Field', 'thegem'),
