function thegem_news_grid_shortcode($atts) {

	if (thegem_is_plugin_active('js_composer/js_composer.php')) {
		global $vc_manager;
		if($vc_manager->mode() == 'admin_frontend_editor' || $vc_manager->mode() == 'admin_page' || $vc_manager->mode() == 'page_editable') {
			return '<div class="news-grid-shortcode-dummy"></div>';
		}
	}

	if (!isset($atts['new_default_highlights'])) {
		if (!isset($atts['news_grid_ignore_highlights'])) {
			$atts['news_grid_ignore_highlights'] = '';
		}
	}

	if (!isset($atts['additional_meta_added'])) {
		if (!isset($atts['news_grid_hide_categories'])) {
			$atts['show_additional_meta'] = '1';
		} else {
			$atts['show_additional_meta'] = '';
		}
		if (isset($atts['news_grid_version']) && $atts['news_grid_version'] == 'list' && !isset($atts['blog_show_readmore_button'])) {
			$atts['show_readmore_button'] = '1';
		}
	}

	$related_by_categories = '';
	if (!empty($atts['related_by_categories'])) {
		$related_by_categories = $atts['related_by_categories'];
	}
	$related_by_tags = '';
	if (!empty($atts['related_by_tags'])) {
		$related_by_tags = $atts['related_by_tags'];
	}
	$related_by_author = '';
	if (!empty($atts['related_by_author'])) {
		$related_by_author = $atts['related_by_author'];
	}

	$source_type = isset($atts['source_type']) ? $atts['source_type'] : '';
	$new_related = isset($atts['new_related']);

	$atts = vc_map_get_attributes('gem_news_grid', $atts);

	$atts['orderby'] = $atts['order_by'];
	unset($atts['order_by']);

	if ($atts['news_grid_version'] !== 'list') {

		$atts['columns_desktop'] = $atts['news_grid_layout'];
		$atts['columns_desktop_builder'] = $atts['news_grid_layout_builder'];
		unset($atts['news_grid_layout']);
		unset($atts['news_grid_layout_builder']);

		$atts['layout'] = $atts['news_grid_style'];
		$atts['layout_builder'] = $atts['news_grid_style_builder'];
		unset($atts['news_grid_style']);
		unset($atts['news_grid_style_builder']);

		if ($atts['layout'] == 'creative') {
			$atts['columns_desktop'] = $atts['columns_desktop_creative'];
			unset($atts['columns_desktop_creative']);
			$atts['columns_100'] = $atts['columns_100_creative'];
			unset($atts['columns_100_creative']);
		} else {
			$atts['columns_100'] = $atts['news_grid_fullwidth_columns'];
			unset($atts['news_grid_fullwidth_columns']);
		}
	}

	$button_params = array();
	foreach ($atts as $key => $value) {
		if (strpos($key, 'news_grid_') !== false) {
			$atts[str_replace('news_grid_','', $key)] = $value;
			unset($atts[$key]);
		}
		if(substr($key, 0, 7) == 'button_') {
			if ( $key == 'button_no_uppercase') {
				$button_params['no-uppercase'] = $value;
			} else {
				$button_params[substr($key, 7)] = $value;
			}
		}
	}
	$atts['button'] = $button_params;

	if (!empty($source_type) && !$new_related) {
		if ($source_type == 'related') {
			$atts['query_type'] = 'related';
			$taxonomy_related = [];
			if ($related_by_categories) {
				$taxonomy_related[] = 'category';
			}
			if ($related_by_tags) {
				$taxonomy_related[] = 'post_tag';
			}
			if ($related_by_author) {
				$taxonomy_related[] = 'authors';
			}
			$atts['taxonomy_related'] = implode(',', $taxonomy_related);
			$atts['exclude_posts_manual_type'] = 'current';
		} else if ($source_type == 'archive') {
			$atts['query_type'] = 'archive';
		} else {
			$atts['query_type'] = 'post';
		}
	}

	ob_start();
	thegem_news_grid($atts);

	$return_html = trim(preg_replace('/\s\s+/', ' ', ob_get_clean()));

	return $return_html;
}

