function thegem_vc_add_element_categories_move_thegem($tabs) {
	$thegem_tab = false;
	$thegem_tab_key = -1;
	foreach($tabs as $key => $tab) {
		if($tab['name'] === __('TheGem', 'thegem')) {
			$thegem_tab = $tab;
			$thegem_tab_key = $key;
		}
	}
	if($thegem_tab_key > -1) {
		unset($tabs[$thegem_tab_key]);
		$new_tabs = array();
		foreach($tabs as $key => $tab) {
			$new_tabs[] = $tab;
			if($tab['filter'] == '*') {
				$new_tabs[] = $thegem_tab;
			}
		}
		$tabs = $new_tabs;
	}
	return $tabs;
}
add_filter('vc_add_element_categories', 'thegem_vc_add_element_categories_move_thegem');

// Fix wpb post types access
function thegem_vc_bc_access_rule_48_post_type_get_state( $state ) {
	if ( null === $state ) {
		$content_types = vc_settings()->get( 'content_types' );
		if ( empty( $content_types ) ) {
			$state = true;
		} else {
			$state = 'custom';
		}
	}

	return $state;
}

function thegem_vc_bc_access_rule_48_post_type_rule( $value, $role, $rule ) {
	if ( ! $role ) {
		return $value;
	}
	global $vc_bc_access_rule_48_editor_post_types;
	$part = vc_role_access()->who( $role->name )->part( 'post_types' );
	if ( ! isset( $part->getRole()->capabilities[ $part->getStateKey() ] ) ) {
		if ( is_null( $vc_bc_access_rule_48_editor_post_types ) ) {
			$pt_array = vc_settings()->get( 'content_types' );
			$vc_bc_access_rule_48_editor_post_types = $pt_array ? $pt_array : vc_default_editor_post_types();
		}

		return in_array( $rule, $vc_bc_access_rule_48_editor_post_types, true );
	}

	return $value;
}

function thegem_vc_bc_access_rule_48() {
	add_filter( 'vc_role_access_with_post_types_get_state', 'thegem_vc_bc_access_rule_48_post_type_get_state' );
	add_filter( 'vc_role_access_with_post_types_can', 'thegem_vc_bc_access_rule_48_post_type_rule', 10, 3 );
	vc_set_default_editor_post_types(array('post', 'page', 'product', 'thegem_news', 'thegem_pf_item', 'thegem_footer', 'thegem_title', 'thegem_templates'));
}
add_action( 'vc_before_init', 'thegem_vc_bc_access_rule_48' );

if (!function_exists('select_post_type_taxonomies')) {
	function select_post_type_taxonomies($post_type = false, $select = false, $add_authors = false) {
		$out = [];
		if ($select) {
			$out = [__('Select taxonomy', 'thegem') => ''];
		}
		if ($post_type && $post_type != 'any') {
			$taxonomies = get_object_taxonomies($post_type, 'objects');
		} else {
			$taxonomies = get_taxonomies(array(
				'public' => true,
			), 'objects');
			$out[__('Portfolio Categories', 'thegem')] = 'thegem_portfolios';
			if ($add_authors) {
				$out[__('Authors', 'thegem')] = 'authors';
			}
		}


		if (empty($taxonomies) || is_wp_error($taxonomies)) {
			return $out;
		}

		foreach ($taxonomies as $taxonomy) {
			if (!empty($taxonomy->name) && !in_array($taxonomy->name, ['product_shipping_class'])) {
				$out[$taxonomy->label] = $taxonomy->name;
			}
		}

		return $out;
	}
}

if (!function_exists('select_portfolio_details')) {
	function select_portfolio_details($select = false) {
		$out = [];
		if ($select) {
			$out = [__('Select field', 'thegem') => ''];
		}
		$details = thegem_get_option('portfolio_project_details');

		if (empty($details)) {
			return $out;
		}

		$details_data = json_decode(thegem_get_option('portfolio_project_details_data'), true);
		if (!empty($details_data)) {
			foreach ($details_data as $v) {
				$out[$v['title']] = '_thegem_cf_' . str_replace('-', '_', sanitize_title($v['title']));
			}
		}

		return $out;
	}
}

if (!function_exists('select_theme_options_custom_fields_all')) {
	function select_theme_options_custom_fields_all($select = false) {
		$out = [];

		if ($select) {
			$out = [__('Select field', 'thegem') => ''];
		}

		$post_types = get_post_types(array(
			'public' => true,
		), 'names');

		foreach ($post_types as $post_type) {
			$out = array_merge($out, select_theme_options_custom_fields($post_type));
		}

		return $out;
	}
}

if (!function_exists('select_theme_options_custom_fields')) {
	function select_theme_options_custom_fields($post_type, $select = false) {
		$out = [];

		if ($select) {
			$out = [__('Select field', 'thegem') => ''];
		}

		switch ($post_type) {
			case 'page':
				$post_type = 'default';
				break;
			case 'thegem_pf_item':
				$post_type = 'portfolio';
				break;
		}

		if (!function_exists('thegem_theme_options_get_page_settings')) return $out;

		$pt_data = thegem_theme_options_get_page_settings($post_type);
		$custom_fields = !empty($pt_data['custom_fields']) ? $pt_data['custom_fields'] : null;

		if (empty($custom_fields)) return $out;

		$custom_fields_data = !empty($pt_data['custom_fields_data']) ? json_decode($pt_data['custom_fields_data'], true) : null;
		if (!empty($custom_fields_data)) {
			foreach ($custom_fields_data as $field) {
				$out[$field['title']] = $field['key'];
			}
		}

		return $out;
	}
}

if (!function_exists('select_post_types')) {
	function select_post_types() {
		$out = [__('Pages', 'thegem') => 'page'];
		$post_types = get_post_types(array(
			'public' => true,
			'_builtin' => false
		), 'objects');

		if (empty($post_types) || is_wp_error($post_types)) {
			return $out;
		}

		foreach ($post_types as $post_type) {
			if (!empty($post_type->name) && !in_array($post_type->name, ['elementor_library', 'thegem_title', 'thegem_footer', 'thegem_templates'])) {
				$out[$post_type->label] = $post_type->name;
			}
		}

		return $out;
	}
}

if (!function_exists('get_thegem_select_taxonomy_terms')) {
	function get_thegem_select_taxonomy_terms($taxonomy, $show_all = false) {

		$out = $show_all ? [__('All', 'thegem') => '0'] : [];

		$terms = get_terms([
			'taxonomy' => $taxonomy,
			'hide_empty' => true,
		]);

		if (empty($terms) || is_wp_error($terms)) {
			return $out;
		}

		foreach ($terms as $term) {
			if (!empty($term->name)) {
				$out[$term->name] = $term->slug;
			}
		}

		return $out;
	}
}
