			$data['label'] = __('Id', 'thegem') . ': ' . $value['id'] . ((strlen($value['name']) > 0) ? ' - ' . __('Name', 'thegem') . ': ' . $value['name'] : '') . ((strlen($value['slug']) > 0) ? ' - ' . __('Slug', 'thegem') . ': ' . $value['slug'] : '');
